import React, { Component } from 'react';
import { Select,Carousel } from 'antd';
import 'antd/dist/antd.css'
// import PropTypes from 'prop-types';
import './index.css'


const contentStyle = {
  height: '160px',
  color: '#fff',
  lineHeight: '160px',
  textAlign: 'center',
  background: '#364d79',
};

const { Option } = Select;

const list = [
    {
        label: 'aa',
        value: 'AA'
    },
    {
        label: 'bb',
        value: 'BB'
    },
    {
        label: 'cc',
        value: 'CC'
    }
]

export default class List extends Component {

    // static propTypes = {
    //     todos:PropTypes.array.isRequired,
    //     updateTodo:PropTypes.func.isRequired,
    //     deleteTodo:PropTypes.func.isRequired,
    handleChange = (value) => {
        console.log(`selected ${value}`);
      }
    render() {
        return (
            <>
            <div className='list'>
                <div className='user'>
                    <a rel='noreferrer' href='https://github.com/reactjs' target='_blank'>
                        <p>
                            <img alt='head_portrait' src="https://img2.baidu.com/it/u=2328905259_3235503559&fm=26&fmt=auto" width='200'/>
                        </p>
                        <p>图像的名字</p>
                    </a>
                </div>
                <div className='user'>
                    <a rel='noreferrer' href='https://github.com/reactjs' target='_blank'>
                        <p>
                            <img alt='head_portrait' src="https://img2.baidu.com/it/u=2328905259_3235503559&fm=26&fmt=auto" width='200'/>
                        </p>
                        <p>图像的名字</p>
                    </a>
                </div>
                <div className='user'>
                    <a rel='noreferrer' href='https://github.com/reactjs' target='_blank'>
                        <p>
                            <img alt='head_portrait' src="https://img2.baidu.com/it/u=2328905259_3235503559&fm=26&fmt=auto" width='200'/>
                        </p>
                        <p>图像的名字</p>
                    </a>
                </div>
            </div>
            <div className='test'>
                    <Select defaultValue="CC" style={{ width: 120 }} onChange={this.handleChange}>
                    {
                        list.map(item => (
                            <Option value={item?.value}>{item?.label}</Option>
                        ))
                    }
                    </Select>
            </div>
            <Carousel autoplay>
                <div>
                <h3 style={contentStyle}>1</h3>
                </div>
                <div>
                <h3 style={contentStyle}>2</h3>
                </div>
                <div>
                <h3 style={contentStyle}>3</h3>
                </div>
                <div>
                <h3 style={contentStyle}>4</h3>
                </div>
            </Carousel>
            </>
        )
    } 

}
