import React, { Component } from 'react';
import { Tabs,Carousel } from 'antd';
import 'antd/dist/antd.css';

const dataitems = [
    {id:'a',pic:'contentStyle pic1',picback:'contentStyle picb',compliment:'孔武有力',time:'20210505'},
    {id:'b',pic:'contentStyle pic2',picback:'contentStyle picb',compliment:'美若天仙',time:'20210505'},
    {id:'c',pic:'contentStyle pic3',picback:'contentStyle picb',compliment:'可可爱爱',time:'20210505'},
    {id:'d',pic:'contentStyle pic4',picback:'contentStyle picb',compliment:'性感妖艳',time:'20210505'},
]
const { TabPane } = Tabs;

function callback(key) {
console.log(key);
}

class Header extends Component {
    render() { 
        return(
            <>
            <Carousel className="zone">
                {
                    dataitems.map((items) => {
                        return (
                            <div className={items.id}>
                                <div className={items.pic}>{items.id}</div>
                                <ul className={items.picback}>
                                    <li className="compliment">{items.compliment}</li>
                                    <li className="time">{items.time}</li>
                                </ul>
                            </div>

                        )
                    })
                }
            </Carousel>
            <div className="shadow"></div>
            <Tabs onChange={callback} type="card">
                <TabPane tab="Tab 1" key="1">
                Content of Tab Pane 1
                </TabPane>
                <TabPane tab="Tab 2" key="2">
                Content of Tab Pane 2
                </TabPane>
                <TabPane tab="Tab 3" key="3">
                Content of Tab Pane 3
                </TabPane>
            </Tabs>,);
            </>
        );
    }
}

export default Header;
