import React, { Component } from 'react';
import store from '../../redux/store';
//引入actionCreator,专门用于创建action对象
import { createIncrementAction } from '../../redux/webdesignActions';

import './webdesign.css'

export default class Webdesign extends Component {
    state = {carname:'宏光mini-ev'}
    componentDidMount(){
        store.subscribe(()=>{
            this.setState({})
        })
    }
    increment = () => {
        const {value} = this.selectNumber
        store.dispatch(createIncrementAction(value))
    }
    incrementaysnc =() => {
        const {value} = this.selectNumber
        setTimeout(()=>{
            store.dispatch(createIncrementAction(value),500)
        })
    }

    render() {
        return (
            <div className='web'>
               <h1>当前求和为{store.getState()}</h1>
               <select ref={c=> this.selectNumber = c}>
                   <option value="1">1</option>
                   <option value="2">2</option>
                   <option value="3">3</option>
               </select>
               <div className="increment" onClick={this.increment}>+</div>
               <div className="increment" onClick={this.increment}>异步加载</div>
            </div>
        )
    }
}
