/* eslint-disable jsx-a11y/anchor-has-content */
import React, { Component } from 'react';
import Search from './components/Search';
import List from './components/List';
// import Antd from './components/Antd';
import './App.css'

const localStorage = window.localStorage
localStorage.setItem('pic','1')

export default class App extends Component {

    //初始化状态
    state = {
        a:''
    }

    picClose = () => {
        if (localStorage.getItem('pic')) {
            this.setState({a:'none'})
        }else {
            localStorage.setItem('pic','1')
            this.setState({a:'none'})
        }
    }

    render() { 
        return (
            <div className='container' >
                <div className='pic' style={{display:localStorage.getItem('pic') ? this.state.a :'block'}}>
                    <a className='slogan' href="https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fimage11.m1905.cn%2Fuploadfile%2F2019%2Fss%2F0409%2F20190409054502756.jpeg&refer=http%3A%2F%2Fimage11.m1905.cn&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=jpeg?sec=1642844530&t=8f6dfd61261203d66921f31305a28cd4">
                        <h2>你是我的小可爱，可可爱爱真可爱</h2>
                    </a>
                    <i onClick={this.picClose} className='close'>X</i>
                </div>
                <div className='wrap'>
                    <Search/>
                    <List/>
                {/* <Antd/> */}
                </div>
            </div>
        )
    }
}
