/* eslint-disable jsx-a11y/anchor-is-valid */
import React from 'react';
import './index.css'
import Headernav from '../Homepage/Headernav';
// import Services from '../Homepage/Container/Services';
import Morecases from '../Homepage/Container/Morecases';
import Footer from '../Homepage/Footer';
import {Scopeservices,Servicesprocess} from '../../data';
import graphicdesign from "../../img/11.jpg";
import interiordesign from "../../img/12.jpg";
import illustration from "../../img/13.jpg";

function Scopeofbusiness() {
    return (
        <div className='scopearea'>
            <Headernav/>
            <div className="scopeofbusiness">
                <div className="intro">
                    <h1>以用户为核心的整合设计解决方案</h1>
                </div>
                <div className="flexbox solution">
                    <div className="tablewrap">
                        <div className="table">
                            <div className="solutitle">
                                <img src={graphicdesign} alt="branddesign" width="53" height="53"/>
                                <h2>平面设计</h2>
                            </div>
                            <hr/>
                            <div className="solucontent">
                                <h3>我们洞察市场需求及用户痛点，为企业建立切实可行的互联网产品策略。</h3>
                            </div>
                        </div>
                    </div>
                </div> 
            </div>
            <div className="services scope">
                <h2>更多服务</h2>
                <div className="line"></div>
                <div className="flexbox servicecontent">
                    {Scopeservices.map(service=>{
                        return (
                            <div className="service" key={service.id}>
                                <div className="servicenum">
                                    <span>{service.id}</span>
                                </div>
                                <h2>{service.h2}</h2>
                                <h3>{service.h3}</h3>
                            </div>
                        )
                    })}
                </div>
            </div>
            <Morecases/>
            <div className="button">
                <a href="#">
                    <span>查看更多</span>
                </a>
            </div>
            <div className="services scope">
                <h2>服务流程</h2>
                <div className="line"></div>
                <div className="flexbox servicecontent">
                    {Servicesprocess.map(service=>{
                        return (
                            <div className="service" key={service.id}>
                                <div className="servicenum">
                                    <span>{service.id}</span>
                                </div>
                                <h2>{service.h2}</h2>
                                <h3>{service.h3}</h3>
                            </div>
                        )
                    })}
                </div>
            </div>
            <Footer/>
        </div>
    )
}

export default Scopeofbusiness
