/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from 'react';
import {nanoid} from 'nanoid';
import { Carousel } from 'antd';
import Joblist from '../Joblist';
import 'antd/dist/antd.css';

const sidebardata = [
    {id:nanoid(),jobtitle:'技术',detail_job:['Java','PHP','C++','区块链','Android','iOS',]},
    {id:nanoid(),jobtitle:'产品',detail_job:['产品总监','产品经理','数据产品经理','游戏策划',]},
    {id:nanoid(),jobtitle:'设计',detail_job:['UI设计师','交互设计','网页设计师','平面设计师',]},
    {id:nanoid(),jobtitle:'运营',detail_job:['新媒体运营','编辑','数据运营','运营总监','COO',]},
    {id:nanoid(),jobtitle:'市场',detail_job:['市场营销','市场推广','市场策划','政府关系',]}, 
    {id:nanoid(),jobtitle:'销售',detail_job:['销售专员','销售经理','销售总监','大客户代表',]}, 
    {id:nanoid(),jobtitle:'职能',detail_job:['HR','行政','财务','审计',]}, 
    {id:nanoid(),jobtitle:'游戏',detail_job:['小游戏开发','U3D','游戏策划','游戏运营',]},       
]

const contentStyle = {
    height: '160px',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    background: '#364d79',
};

export default class Container extends Component {
    render() { 
        return (
            <div className="container">
                <div className="clearfix">
                    <div className="sidebar">
                        {
                            sidebardata.map(item => {
                                return(
                                    <div className="sub_sidebar">
                                        <span className="jobtitle">{item.jobtitle}</span>
                                        {item.detail_job.map( job =>{
                                            return(<a href="#" className="detail_job">{job}</a>)
                                        })}
                                        <i className="iconfont icon-youjiantou"></i>
                                    </div>
                                )
                            })
                        }
                    </div>
                    <div className="banner_bg">
                        <Carousel autoplay className="banner_bg_1">
                        <div href="#" style={contentStyle}>
                            <img src="https://www.lgstatic.com/i/image6/M01/68/D9/Cgp9HWHCzkGAQebjAAHgEcL_Kqg880.PNG" alt="#" width='694px' height='286px'></img>
                        </div>
                        <div href="#" style={contentStyle}>
                            <img src="https://www.lgstatic.com/i/image6/M01/68/D9/Cgp9HWHCzkGAQebjAAHgEcL_Kqg880.PNG" alt="#"></img>
                        </div>
                    </Carousel>
                    </div>
                    <Joblist />
                </div>
            </div>
        );
    }
};
