import './index.css'

export default class Header extends Component {

    //初始化状态
    state = {
        keyword:'',
    }

    // myRef = createRef()

    getKeyword = (event) => {
        this.setState({keyword:event.target.value})
    }

    bookSearch = ()=> {
        const {keyword} = this.state
        console.log(keyword);
    }

    render() { 
        return (
            <div>
                <input onChange={this.getKeyword} type="text" />
                <button onClick={this.bookSearch}>搜索</button>
                <button>排序</button>
            </div>
        );
    }
}
