import React from 'react';
import './index.css'

var services = [
    {id:'1',h2:'网站搭建',h3:'提供从网站建设开发到域名、空间、备案、售后维护的一体化设计、一站式解决方案。'},
    {id:'2',h2:'APP开发',h3:'准确把握产品定位及传播受众需求，完美实现您的想法。'},
    {id:'3',h2:'3D渲染',h3:'精致、真实的3D渲染，提供以假乱真的3D效果展示。'},
    {id:'4',h2:'海报设计',h3:'商业性与时尚性相结合，设计全面、高效、优质、推广性强的商业海报。'},
    {id:'5',h2:'VI打造',h3:'根据品牌特点、进行兼具美感和品牌个性的全方位推广。'},
    {id:'6',h2:'插画手绘',h3:'卡通、唯美、现代、古典……为您绘制多风格手绘插画。'},
]
function Services() {
    return (
        <div className="services">
            <h2>更多服务</h2>
            <div className="line"></div>
            <div className="flexbox servicecontent">
                {services.map(service => {
                    return (
                        <div className="service">
                            <div className="servicenum">
                                <span>{service.id}</span>
                            </div>
                            <h2>{service.h2}</h2>
                            <h3>{service.h3}</h3>
                        </div>
                    )
                })}
            </div>
        </div>
    )
}

export default Services
