import React, { Component } from 'react';
import axios from 'axios';
import PubSub from 'pubsub-js';
// import PropTypes from 'prop-types';
import './index.css'

export default class Search extends Component {

    search = () => {
        const {keyWordElement:{value:keyword}} = this;
        // this.props.updateAppState({isFirst:false,isLoading:true})
        PubSub.publish('github',{isFirst:false,isLoading:true})
        this.setState({a:'flex'})
        axios.get(`https://api.github.com/search/users?q=${keyword}`).then(
            res => {
                // this.props.updateAppState({isLoading:false,users:res.data.items});
                PubSub.publish('github',{isLoading:false,users:res.data.items});
            },
            error => {
                // this.props.updateAppState({isLoading:false,err:error});
                PubSub.publish('github',{isLoading:false,err:error});
            }
        )
    }

    render() { 
        return (
            <div className='search'>
                <label>
                    <input ref={c => this.keyWordElement = c} type='text' placeholder='请输入关键词'/>
                    <button onClick={this.search} className='btn'>搜索github头像</button>
                </label>
            </div>
        )
    }
}
