export default class Container extends Component {
    render() { 
        return (
            <div className="container">
                <div className="clearfix">
                    <div className="sidebar">
                        {
                            sidebardata.map(item => {
                                return(
                                    <div className="sub_sidebar">
                                        <span className="jobtitle">{item.jobtitle}</span>
                                        {item.detail_job.map( job =>{
                                            return(<a href="#" className="detail_job">{job}</a>)
                                        })}
                                        <i className="iconfont icon-youjiantou"></i>
                                    </div>
                                )
                            })
                        }
                    </div>
                    <div className="banner_bg">
                        <li className="banner_bg_1">
                            <a href="#">
                                <img src="https://www.lgstatic.com/i/image6/M01/68/D9/Cgp9HWHCzkGAQebjAAHgEcL_Kqg880.PNG" alt="#"></img>
                            </a>
                        </li>
                    </div>
                    <Joblist />
                </div>
            </div>
        );
    }
};
