import React, { Component } from 'react';
import { Select,Carousel } from 'antd';
import {nanoid} from 'nanoid';
import 'antd/dist/antd.css';

const contentStyle = {
    height: '160px',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    background: '#364d79',
  };

  const { Option } = Select;

  const list = [
      {
          label: 'aa',
          value: 'AA'
      },
      {
          label: 'bb',
          value: 'BB'
      },
      {
          label: 'cc',
          value: 'CC'
      }
  ]

  export default class Antd extends Component {

    handleChange = (value) => {
        console.log(`selected ${value}`);
      }

    render() {
        return (
            <>
            <div className='select'>
                    <Select defaultValue="CC" style={{ width: 120 }} onChange={this.handleChange}>
                    {
                        list.map(item => (
                            <Option key={nanoid()} value={item?.value}>{item?.label}</Option>
                        ))
                    }
                    </Select>
            </div>
            <Carousel autoplay>
                <div>
                <h3 style={contentStyle}>1</h3>
                </div>
                <div>
                <h3 style={contentStyle}>2</h3>
                </div>
                <div>
                <h3 style={contentStyle}>3</h3>
                </div>
                <div>
                <h3 style={contentStyle}>4</h3>
                </div>
            </Carousel>
            </>
        )
    } 

}
