import { INCREMENT } from "./constants"
const initState = 0
const webdesginReducer = (preState=initState,action)=>{
    //从action 对象中获取tpye和data
    const {type,data} =action
    //根据type决定如何加工数据
    switch(type){
        case INCREMENT:
            return preState+data
        case 'decrement':
            return preState-data
        default:
            return preState
    }
}
export default webdesginReducer
