/* eslint-disable jsx-a11y/anchor-is-valid */
import React from 'react';
import { d_pic } from '../../../data';
import { Carousel } from 'antd';
import 'antd/dist/antd.css';
import './Designer_exhibition.css'


function Designer_exhibition() {
    return (
        <div className="designer_exhibition">
            <h2>设计师作品</h2>
            <div className="line"></div>
            <div className="flexbox des_exhi">
                {d_pic.map(pic => {
                    return(
                        <div className="exhibipic" key={pic.index}>
                            <div className="designpic">
                                <a href="#" className="designpicture">
                                    <img src={pic} alt={pic.index} />
                                </a>
                            </div>
                        </div>
                    )
                })}
            </div>
            <Carousel>
                {d_pic.map(pic => {
                    return(
                        <div>
                            <div className="dpic" style={{backgroundImage:`url(${pic})`}}></div>
                        </div>
                    )
                })}
            </Carousel>
        </div>
    )
}

export default Designer_exhibition
