import React, { Component } from 'react';
import pubSub from 'pubsub-js';
import './index.css';

const pic_url='https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fwww.kfzimg.com%2FG07%2FM00%2FBD%2F7D%2Fq4YBAFx-Ow6AdHkVAAHBhXpIX5U130_b.jpg&refer=http%3A%2F%2Fwww.kfzimg.com&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=jpeg?sec=1642930093&t=3a80f3bab0f385d2b18d6c55200e928f'

export default class Items extends Component {

    state = {
        items:[
            {id:1, name:'高等数学',pic_path:pic_url},
            {id:2, name:'计算机网络',pic_path:pic_url},
            {id:3, name:'物联网',pic_path:pic_url},
            {id:4, name:'高等数学2',pic_path:pic_url}
        ]
    }

    componentDidMount(){
        pubSub.subscribe('book',(msg,index)=>{
            const {items} =this.state
            const newItems = items.filter((a)=>{
                return a.name === index
            })
            this.setState({items:newItems})
        })

    }

    render() { 
        const {items} =this.state
        return (
            items.map( item => {
                return (
                    <li key={item.id}>
                        <img src={item.pic_path} alt={item.name} width='200px'/>
                        <p>{item.name}</p>
                    </li>
                )

            })
        );
    }
}
