/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from 'react';
import { Tabs,Carousel, Table, Tag, Space } from 'antd';
import 'antd/dist/antd.css';

const dataitems = [
    {id:'a',pic:'contentStyle pic1',picback:'contentStyle picb',compliment:'孔武有力',time:'20210505'},
    {id:'b',pic:'contentStyle pic2',picback:'contentStyle picb',compliment:'美若天仙',time:'20210505'},
    {id:'c',pic:'contentStyle pic3',picback:'contentStyle picb',compliment:'可可爱爱',time:'20210505'},
    {id:'d',pic:'contentStyle pic4',picback:'contentStyle picb',compliment:'性感妖艳',time:'20210505'},
]
const { TabPane } = Tabs;

const columns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      render: text => <a>{text}</a>,
    },
    {
      title: 'Age',
      dataIndex: 'age',
      key: 'age',
    },
    {
      title: 'Address',
      dataIndex: 'address',
      key: 'address',
    },
    {
      title: 'Tags',
      key: 'tags',
      dataIndex: 'tags',
      render: tags => (
        <>
          {tags.map(tag => {
            let color = tag.length > 5 ? 'geekblue' : 'green';
            if (tag === 'loser') {
              color = 'volcano';
            }
            return (
              <Tag color={color} key={tag}>
                {tag.toUpperCase()}
              </Tag>
            );
          })}
        </>
      ),
    },
    {
      title: 'Action',
      key: 'action',
      render: (text, record) => (
        <Space size="middle">
          <a>Invite {record.name}</a>
          <a>Delete</a>
        </Space>
      ),
    },
  ];

const data = [
{
    key: '1',
    name: 'John Brown',
    age: 32,
    address: 'New York No. 1 Lake Park',
    tags: ['nice', 'developer'],
},
{
    key: '2',
    name: 'Jim Green',
    age: 42,
    address: 'London No. 1 Lake Park',
    tags: ['loser', 'broker'],
},
{
    key: '3',
    name: 'Joe Black',
    age: 32,
    address: 'Sidney No. 1 Lake Park',
    tags: ['cool', 'teacher', 'baby'],
},
];

function callback(key) {
console.log(key);
}

class Header extends Component {
    render() { 
        return(
            <>
            <Carousel className="zone">
                {
                    dataitems.map((items) => {
                        return (
                            <div className={items.id}>
                                <div className={items.pic}>{items.id}</div>
                                <ul className={items.picback}>
                                    <li className="compliment">{items.compliment}</li>
                                    <li className="time">{items.time}</li>
                                </ul>
                            </div>

                        )
                    })
                }
            </Carousel>
            <div className="shadow"></div>
            <Tabs onChange={callback} type="card">
                <TabPane tab="Tab 1" key="1">
                    <Table columns={columns} dataSource={data}/>
                </TabPane>
                <TabPane tab="Tab 2" key="2">
                    <Table columns={columns} dataSource={data}/>
                </TabPane>
                <TabPane tab="Tab 3" key="3">
                    <Table columns={columns} dataSource={data}/>
                </TabPane>
            </Tabs>
            </>
        );
    }
}

export default Header;
