import React, { Component } from 'react';
// import PropTypes from 'prop-types';
import './index.css'

export default class List extends Component {

    // static propTypes = {
    //     todos:PropTypes.array.isRequired,
    //     updateTodo:PropTypes.func.isRequired,
    //     deleteTodo:PropTypes.func.isRequired,


    render() {
        const {users,isFirst,isLoading,err} = this.props
        const promise = Promise(users.map( user => {
            return(
                <div key={user.id} className='user'>
                    <a rel='noreferrer' href={user.html_url} target='_blank'>
                        <p>
                            <img alt='head_portrait' src={user.avatar_url} width='200'/>
                        </p>
                        <p>{user.login}</p>
                    </a>
                </div>
            )
        }).then(() => {
            console.log('成功');
        }
        ))
        return (
            <>
            {/* <h2 style={{marginTop:'20px'}}>共搜寻到{users.length}位相关账户如下：</h2> */}
            <div className='list'>
                {

                    isFirst ? <h2 style={{margin:0}}>欢迎使用，输入关键字，随后点击搜索</h2> :
                    isLoading ? <h2>加载中。。。</h2> :
                    err ? <h2 style={{color:'red'}}>{err}</h2> : promise
                }
            </div>
            </>
        )
    } 

}
