import React, { Component, createRef } from 'react';
import pubSub from 'pubsub-js';
import './index.css'

export default class Header extends Component {

    //初始化状态
    state = {
        keyword:'',
    }

    myRef = createRef()
    bookSearch = ()=> {
        pubSub.publish('book',this.myRef.current.value);
        pubSub.publish('search',{search:true});
    }

    render() { 
        return (
            <div>
                <input ref={this.myRef} type="text" />
                <button onClick={this.bookSearch}>搜索</button>
                <button>排序</button>
            </div>
        );
    }
}
