import {INCREMENT} from './constants'

//同步action
export const createIncrementAction = data =>({type:INCREMENT,data})

//异步action
export const createIncrementAsyncAction = (data,time) => {
    return (dispatch)=> {
        setTimeout(()=>{
            dispatch(createIncrementAction(data))
        },time)
    }
}
