import React, { Component } from 'react';
import './index.css';

export default class Items extends Component {

    state = {
        items:[
            {id:1, name:'高等数学',pic_path:'../../Advanced_mathmaticsI.jpg'},
            {id:2, name:'计算机网络',pic_path:'../Advanced_mathmaticsI.jpg'},
            {id:3, name:'物联网',pic_path:'../Advanced_mathmaticsI.jpg'},
            {id:4, name:'高等数学2',pic_path:'../Advanced_mathmaticsI.jpg'}
        ]
    }
    render() { 
        const {items} = this.state
        return (
            items.map( item => {
                return (
                    <li key={item.id}>
                        <img src={item.pic_path} alt={item.name} width='200px'/>
                        <p>{item.name}</p>
                    </li>
                )

            })
        );
    }
}
