import React, { Component } from 'react';
import Search from './components/Search';
import List from './components/List';
// import Antd from './components/Antd';
import './App.css'


export default class App extends Component {

    //初始化状态
    state = {
        users : [],
        isFirst:true,
        isLoading:false,
        err:'',
    }

    updateAppState = stateObj => {
        this.setState(stateObj)
    }

    render() { 
        return (
            <div className='container' >
                <div className='isShow'>
                    <img onClick={this.hiddenpic} src="https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fimg.jj20.com%2Fup%2Fallimg%2Ftp09%2F21042G4331941H-0-lp.jpg&refer=http%3A%2F%2Fimg.jj20.com&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=jpeg?sec=1642841216&t=57603779e554369781199c2580a9df60" alt="向日葵" />
                </div>
                <div className='wrap'>
                    <Search updateAppState={this.updateAppState} users={this.state.users}/>
                    <List {...this.state}/>
                {/* <Antd/> */}
                </div>
            </div>
        )
    }
}
