/* eslint-disable jsx-a11y/anchor-has-content */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from 'react';
import pubSub from 'pubsub-js';
import './index.css';
import go_top from '../../img/go-top.png';
import go_top_fire from '../../img/go-top-fire.png';

export default class Header extends Component {

    state = {
        header:false,
        search:false,
        timer:0,
    }
    myref = React.createRef();

    componentDidMount() {
        window.addEventListener('scroll', this.handleScroll);
    }

    handleScroll=()=> {
        const data = document.documentElement.scrollTop||document.body.scrollTop;
        if(data>=78){
            this.setState({header:true,search:true});
            pubSub.publish('scroll',{header:true});
        }
        else {
            this.setState({header:false,search:false});
            pubSub.publish('scroll',{header:false});
        }
    }

    go_top =()=>{
        var {timer} = this.state;
        // clearInterval(timer)
        // this.setState({timer:setInterval(()=>{window.scrollBy(0,-2)},10)})
        // const data = document.documentElement.scrollTop||document.body.scrollTop;
        // if(data===0){
        //     clearInterval(timer)
        // }
        cancelAnimationFrame(timer);
        timer=requestAnimationFrame(function fn(){
        var oTop = document.body.scrollTop || document.documentElement.scrollTop;
        if(oTop > 0){window.scrollBy(0,-1);timer = requestAnimationFrame(fn);}
        else{cancelAnimationFrame(timer);} 
        });
    }
    render() { 
        const {header,search} = this.state
        return(
          <div className="header">
            <div className={search ? "top_banner top_banner_fixed":"top_banner"}>
                <a ref="nofollow" href="https://kaiwu.lagou.com/course/courseInfo.htm?courseId=1261&decorateId=d4328b75c7f247888eddd15c9dd27c38&utm_source=lagou&utm_medium=pctopbanner&utm_campaign=dcnlzdk&_channel_track_key=e6yncXip#/sale"></a>
            </div>
            <div className={header ? "lg_header header-fixed":"lg_header"}>
                <div className="inner">
                    <div className="lg_tbar">
                        <a href="https://www.lagou.com/" className="logo"> </a>
                        <ul className="lg_tbar_tabs">
                            <li className="suggest_city" id="changeCity_btn">
                                <a href="#" className="citys">杭州站</a>
                                <i className="iconfont icon-xiajiantou icon-city"></i>
                            </li>
                            <li className="detail_page">
                                <a href="#" >首页</a>
                            </li>
                            <li>
                                <a href="#" className="pos">职位</a>
                            </li>
                            <li >
                                <a href="#" className="company">公司</a>
                            </li>
                            <li >
                                <a href="#" className="screw">校园招聘</a>
                            </li>
                            <li >
                                <a href="#" className="courses">课程</a>
                            </li>
                            <li >
                                <a rel="noreferrer" href="https://help.lagou.com/staticpage/app_download/pc/index.html" className="icon-app" target="_blank">
                                    拉勾APP
                                </a>
                            </li>
                        </ul>
                        <ul className="lg_tbar_users">
                            <li className="notes">
                                <i className="iconfont icon-lingdang-xianxing"></i>
                            </li>
                            <li className="resume">
                                <a href="#">我的简历</a>
                            </li>
                            <li className="user_info">
                                <span>甘星</span>
                                <i className="iconfont icon-xiajiantou right-arrow"></i>
                                <ul className="account">
                                    <li className="account_set">
                                        <a href="#">账号设置</a>
                                    </li>
                                    <li className="account_set">
                                        <a href="#">退出</a>
                                    </li>
                                </ul>
                            </li>
                            <li className="entreprise"> 
                                <a href="#">进入企业版</a>
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
            <div  ref={this.myref}  className="rightNav">
                    <div className="rightNav_wrap">
                        <a href="#" className="iconfont icon-kongxinwujiaoxing"></a>
                        <a href="#" className="iconfont icon-zhifeiji1"></a>
                        <a href="#" className="iconfont icon-dingyue"></a>
                        <a href="#" className="iconfont icon-HRyaoyue"></a>
                        <a href="#" className="iconfont icon-edit"></a>
                        <a href="#" className="iconfont icon-ico"></a>
                    </div>
                </div>
            <div className={search ? "search search-fixed":"search"}>
                <div className="search_wrap">
                    <div className="middle">
                      <input type="text" placeholder="简历诊断"></input>
                      <button>搜索</button>
                      <p className={search ? "ad ad-hidden":"ad"}>
                      <span>热门搜索：</span>
                      <a href="#">急速进大厂</a>
                      </p>
                    </div>
                </div>
            </div>
            <div className="gotop">
                <img class="go_top" src={go_top} alt="#" width="28" height="46"/>
                <img onClick={this.go_top} class="go_top_fire" src={go_top_fire} alt="#" width="28" height="46"/>
            </div>
          </div>
        );
    }
};
